% parameters: dimension of parameters% return: random mean and covariancefunction [M,Sigma] = generate_para(d)  % generate random mean  % for conveniece, make them integers between [-5,5]  M = randi([-5,5],d,1);    % generate random a,b,c,alpha  % check if the rank is equal to the dimension  % to make sure the covariance matrix is invertible  r = 0;  while r<d    % for conveniece, make a,b,c integers between [1,5]    [a,b,c] = num2cell(randi([1,5],1,3)){:};    % for conveniece, make alpha 1-decimal between [0.1,0.9]    alpha = randi([1,10])/10;    % generate random covariance matrix based on a,b,c,alpha    Sigma = [a^2, alpha*a*b, alpha*a*c;             alpha*a*b, b^2, alpha*b*c;             alpha*a*c, alpha*b*c, c^2];    r = rank(Sigma);  end  end