% parameters: samples of the two class, number of data points%             the two dimensions to plot the data% return: data points for plotting discriminant functionfunction dis = hk_discriminant_2d(samples1,samples2,npoints,d1,d2)  % IMPORTANT  % train new parameters specifically for 2-d  % do not use trained parameters for d-dimension data  % the projection of discriminant function to 2-d doesn't make sense  samples1 = samples1(:,[d1,d2]);  samples2 = samples2(:,[d1,d2]);  alpha=0.5; nepoch=1000;  [a,b] = train_hk(samples1,samples2,alpha,nepoch);    samples = [ samples1 ; samples2 ];  mini = min(samples)(1);  maxi = max(samples)(1);  %mini=8;maxi=10;    % a[0]+a[1]*x[1]+a[2]*x[2]=0  % x[2] = -(a[0]+a[1]*x[1])/a[2]  step = (maxi-mini)*3/2/npoints;  st = mini-((maxi-mini)/4);    for i = 1:(npoints+1)    x = st+(i-1)*step;    y = -(a(1)+a(2)*x)/a(3);    dis(i,1) = x;    dis(i,2) = y;  endend