% parameters: training samples% return: maximum likelihood estimated mean and covariancefunction [mean,cov] = maximum_likelihood(samples)  % assume samples are of Gaussian distribution  % get the number of samples and dimension  [n,d] = size(samples);    % maximum likelihood estimation of the mean   mean = (sum(samples)./n)';    % maximum likelihood estimation of the covariance  % bias, replace (m) with (m-1) to get unbias estimation)  cov = (samples.-mean')'*(samples.-mean')./n;end