% parameters: convergence data about mean of the two classes% return: plot convergence of mean in x1,x2,x3 domain separatelyfunction plot_convergence(convergence1, convergence2, step)  figure  % x axis is the number of training samples  [n,d] = size(convergence1);  x = linspace(step,n*step,n);  % plot convergence of class-1 mean  subplot(3,2,1)  plot(x,convergence1(:,1),'r');  title('convergence of class-1 mean in x1 domain')  subplot(3,2,3)  plot(x,convergence1(:,2),'g');  title('convergence of class-1 mean in x2 domain')  subplot(3,2,5)  plot(x,convergence1(:,3),'b');  title('convergence of class-1 mean in x3 domain')    % plot convergence of class-2 mean  subplot(3,2,2)  plot(x,convergence2(:,1),'r');  title('convergence of class-2 mean in x1 domain')  subplot(3,2,4)  plot(x,convergence2(:,2),'g');  title('convergence of class-2 mean in x2 domain')  subplot(3,2,6)  plot(x,convergence2(:,3),'b');  title('convergence of class-2 mean in x3 domain')end