% parameters: random vectors of the two classes and points for drawing discriminant function% return: plot figurefunction plot_data_discriminant(rv_1,rv_2,dis_1_2,dis_1_3)  figure  % x1-x2 domain  subplot(2,2,1)  scatter(rv_1(:,1),rv_1(:,2),'red')  hold on  scatter(rv_2(:,1),rv_2(:,2),'blue')  hold on  plot(dis_1_2(:,1),dis_1_2(:,2),'c')  hold off  title('discriminant function in x1-x2 domain')  %axis equal  % x1-x3 domain  subplot(2,2,2)  scatter(rv_1(:,1),rv_1(:,3),'red')  hold on  scatter(rv_2(:,1),rv_2(:,3),'blue')  hold on  plot(dis_1_3(:,1),dis_1_3(:,2),'c')  hold off  title('discriminant function in x1-x3 domain')  %axis equal  % x1-x2 domain  subplot(2,2,3)  scatter(rv_1(:,1),rv_1(:,2),'red')  hold on  scatter(rv_2(:,1),rv_2(:,2),'blue')  hold on  plot(dis_1_2(:,1),dis_1_2(:,3),'m')  hold off  title('discriminant function in x1-x2 domain')  %axis equal  % x1-x3 domain  subplot(2,2,4)  scatter(rv_1(:,1),rv_1(:,3),'red')  hold on  scatter(rv_2(:,1),rv_2(:,3),'blue')  hold on  plot(dis_1_3(:,1),dis_1_3(:,3),'m')  hold off  title('discriminant function in x1-x3 domain')  %axis equalend