% parameters: training samples of the two classes, training step size,%             the true covariance of data and initial guess of parameter to estimate% return: bayesian estimation convergence data about mean of the two class%         estimated mean of the two classes (covariance is assumed known)function [convergence1,convergence2,m1,m2] = train_be(samples1,samples2,step,sig1,sig2)  % get size of samples, assume the two class have same size of samples  [n,d] = size(samples1);  % collect estimation every 5 samples  nstep = n/step;  convergence1 = zeros(nstep,d);  convergence2 = zeros(nstep,d);  % collect convergence data about mean  for i = 1:nstep    [m1,c1] = bayesian_estimation(samples1(1:i*step,:),sig1);    convergence1(i,:) = m1';    [m2,c2] = bayesian_estimation(samples2(1:i*step,:),sig2);    convergence2(i,:) = m2';  endend