% parameters: training samples of the two classes% return: fisher's ratio and discriminant thresholdfunction [w,c] = train_fisher(samples1,samples2)  % get size of both sample sets  [n1,d1] = size(samples1);  [n2,d2] = size(samples2);  % calculate the mean and covariance of both classes  mean1 = mean(samples1); mean2 = mean(samples2);  scatter1 = cov(samples1).*(n1-1); scatter2 = cov(samples2).*(n2-1);  % calculate fisher's ratio and discriminant threshold  w = inv(scatter1.+scatter2)*(mean1-mean2)';  c = ((1./2).*(mean1+mean2))*w;end