% parameters: training samples of the two classes and training step size% return: maximum likelihood convergence data about mean of the two class%         estimated mean and covariance of the two classesfunction [convergence1,convergence2,m1,m2,c1,c2] = train_ml(samples1, samples2, step)  % get size of samples, assume the two class have same size of samples  [n,d] = size(samples1);  % collect estimation every 5 samples  nstep = n/step;  convergence1 = zeros(nstep,d);  convergence2 = zeros(nstep,d);  % collect convergence data about mean  for i = 1:nstep    [m1,c1] = maximum_likelihood(samples1(1:i*step,:));    convergence1(i,:) = m1';    [m2,c2] = maximum_likelihood(samples2(1:i*step,:));    convergence2(i,:) = m2';  endend